ServiceDefinition.S:
{
	"id": "FloodMapping",
	"version": "1.0.0",
	"title": "{MAIN_TITLE}",
	"description": "{MAIN_DESCRIPTION}",
	"inputs": {
		"bounding_box": {
			"schema": {
				"format": "ogc-bbox",
				"type": "object",
				"required": [
					"bbox"
				],
				"properties": {
					"crs": {
						"default": "http://www.opengis.net/def/crs/OGC/1.3/CRS84",
						"format": "uri",
						"type": "string",
						"enum": [
							"http://www.opengis.net/def/crs/OGC/1.3/CRS84",
							"http://www.opengis.net/def/crs/OGC/0/CRS84h"
						]
					},
					"bbox": {
						"oneOf": [
							{
								"minItems": 4,
								"maxItems": 4
							},
							{
								"minItems": 6,
								"maxItems": 6
							}
						],
						"type": "array",
						"items": {
							"type": "number"
						}
					}
				}
			},
			"minOccurs": 1,
			"description": "{AOI_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{AOI}",
			"required": true
		},
		"source_data": {
			"schema": {
				"type": "string",
				"enum": [
					"hrdem-mosaic-1m",
					"hrdem-mosaic-2m"
				]
			},
			"minOccurs": 1,
			"description": "{SOURCE_DATA_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{SOURCE_DATA_TITLE}",
			"required": true
		},
		"sea_level_rise": {
			"schema": {
				"default": 3,
				"format": "double",
				"type": "number",
				"minimum": 0
			},
			"minOccurs": 1,
			"description": "{SEA_LEVEL_RISE_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{SEA_LEVEL_RISE_TITLE}",
			"required": true
		},
        "include_tide": {
			"schema": {
				"default": "true",
				"type": "string",
                "enum": [
					"true",
					"false"
				]
			},
			"minOccurs": 1,
			"description": "{INCLUDE_TIDE_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{INCLUDE_TIDE_TITLE}",
			"required": true
		}
	},
	"outputs": {
		"flood_mapping_nrcan_tiff": {
			"schema": {
				"contentMediaType": "image/tiff",
				"contentEncoding": "binary",
				"type": "string"
			},
			"minOccurs": 1,
			"description": "{OUTPUT_TIFF_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{OUTPUT_TIFF_TITLE}",
			"required": true
		},
		"flood_mapping_nrcan_shapefile": {
			"schema": {
				"contentMediaType": "application/vnd.shp",
				"contentEncoding": "binary",
				"type": "string"
			},
			"minOccurs": 1,
			"description": "{OUTPUT_SHP_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{OUTPUT_SHP_TITLE}",
			"required": true
		},
		"flood_mapping_nrcan_geojson": {
			"schema": {
				"contentMediaType": "application/geo+json",
				"contentEncoding": "binary",
				"type": "string"
			},
			"minOccurs": 1,
			"description": "{OUTPUT_GEOJSON_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{OUTPUT_GEOJSON_TITLE}",
			"required": true
		},
		"flood_mapping_nrcan_kml": {
			"schema": {
				"contentMediaType": "application/vnd.google-earth.kml+xml",
				"contentEncoding": "binary",
				"type": "string"
			},
			"minOccurs": 1,
			"description": "{OUTPUT_KML_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{OUTPUT_KML_TITLE}",
			"required": true
		}
	},
	"response": [
		"raw"
	],
	"links": [
		{
			"rel": "{DESCRIPTION_LINK_REL_EN}",
			"href": "https://{HOST}/Prod/processes/FloodMapping?lang=en",
			"type": "application/json",
			"title": "Process Description for Flood Mapping",
            "hreflang": "en"
		},
        {
			"rel": "{DESCRIPTION_LINK_REL_FR}",
			"href": "https://{HOST}/Prod/processes/FloodMapping?lang=fr",
			"type": "application/json",
			"title": "FR_Process Description for Flood Mapping",
            "hreflang": "fr"
		},
		{
			"rel": "http://www.opengis.net/def/rel/ogc/1.0/execute",
			"href": "https://{HOST}/Prod/processes/FloodMapping/execution?lang=en",
			"title": "Process Execution for Flood Mapping",
            "hreflang": "en"
		},
        {
			"rel": "http://www.opengis.net/def/rel/ogc/1.0/execute",
			"href": "https://{HOST}/Prod/processes/FloodMapping/execution?lang=fr",
			"title": "FR_Process Execution for Flood Mapping",
            "hreflang": "fr"
		}
	],
	"outputTransmission": [
		"reference"
	],
	"jobControlOptions": [
		"async-execute"
	]
}
