i18nStrings.S:
{
    "MAIN_TITLE": {
        "fr": "Cartographie des zones inondables",
        "en": "Flood Mapping"
    },
    "MAIN_DESCRIPTION": {
        "fr": "Ce processus permet à un utilisateur de spécifier une élévation attendue du niveau de la mer sur une zone d'intérêt spécifiée, en renvoyant une carte GeoTIFF, Shapefile, KML ou des données GeoJSON représentant des terres qui seraient inondées.",
        "en": "This process allows a user to specify a expected sea level rise over a specified area of interest, returning a GeoTIFF, Shapefile, KML map, or GeoJSON data representing land that would be flooded."
    },
    "AOI_DESCRIPTION": {
        "fr": "Zone d'intérêt pour laquelle les données sont demandées. Les données actuelles couvrent l'est du Canada, y compris, mais sans s'y limiter, le sud du Québec.",
        "en": "The area of interest that the data is being requested for. The current data is found over Eastern Canada, including but not limited to southern Quebec."
    },
    "AOI": {
        "fr": "Domaine d'intérêt",
        "en": "Area of Interest"
    },
    "SOURCE_DATA_TITLE": {
        "fr": "Source des données d'élévation",
        "en": "Elevation Data Source"
    },
    "SOURCE_DATA_DESCRIPTION": {
        "fr": "Quelle couche de mosaïque de modèle numérique d'élévation à haute résolution (mosaïque HRDEM) de la bibliothèque du catalogue d'actifs spatio-temporels (STAC) de RNCAN utiliser pour calculer les données de cartographie des inondations.",
        "en": "Which High Resolution Digital Elevation Model Mosaic (HRDEM Mosaic) layer from the NRCAN SpatioTemporal Asset Catalog (STAC) library to use to calculate the flood mapping data."
    },
    "SEA_LEVEL_RISE_DESCRIPTION": {
        "fr": "L'élévation du niveau de la mer est une augmentation du niveau des océans due aux effets du réchauffement climatique. Les valeurs valides sont tout nombre positif.",
        "en": "Sea level rise is an increase in the level of the worlds oceans due to the effects of global warming. Valid values are any positive number."
    },
    "SEA_LEVEL_RISE_TITLE": {
        "fr": "Élévation du niveau de la mer en mètres",
        "en": "Sea Level Rise in Metres"
    },
    "INCLUDE_TIDE_DESCRIPTION": {
        "fr": "Inclure ou non le niveau de marée accru dû à l'élévation donnée du niveau de la mer en tant que polygone distinct dans la sortie demandée.",
        "en": "Whether or not to include the increased tide level from the given rise in sea level as separate data in the requested output."
    },
    "INCLUDE_TIDE_TITLE": {
        "fr": "Inclure les données de marée",
        "en": "Include Tide Data"
    },
    "OUTPUT_TIFF_DESCRIPTION": {
        "fr": "Un format GeoTIFF dans lequel chaque pixel représente une zone de terre qui sera bleue si la zone est couverte par le nouveau niveau de la mer ou (éventuellement) rouge si la zone est couverte par le nouveau niveau de marée.",
        "en": "A GeoTIFF format with each pixel representing an area of land which will be blue if the area is covered by the new sea level or (optionally) red if the area is covered by the new tide level."
    },
    "OUTPUT_TIFF_TITLE": {
        "fr": "Cartographie des zones inondables GeoTIFF",
        "en": "Flood Mapping GeoTIFF"
    },
    "OUTPUT_KML_DESCRIPTION": {
        "fr": "Un format kml avec chaque polygone représentant une zone de terre qui sera bleue si la zone est couverte par le nouveau niveau de la mer ou (facultativement) rouge si la zone est couverte par le nouveau niveau de marée.",
        "en": "A kml format with each polygon representing an area of land which will be blue if the area is covered by the new sea level or (optionally) red if the area is covered by the new tide level."
    },
    "OUTPUT_KML_TITLE": {
        "fr": "Cartographie des zones inondables KML",
        "en": "Flood Mapping KML"
    },
    "OUTPUT_SHP_DESCRIPTION": {
        "fr": "Un format de fichier de formes dans lequel chaque polygone représente une zone de terre qui sera bleue si la zone est couverte par le nouveau niveau de la mer ou (facultativement) rouge si la zone est couverte par le nouveau niveau de marée.",
        "en": "A shapefile format with each polygon representing an area of land either covered by the new sea level or (optionally) covered by the new tide level."
    },
    "OUTPUT_SHP_TITLE": {
        "fr": "Cartographie des zones inondables Shapefile",
        "en": "Flood Mapping Shapefile"
    },
    "OUTPUT_GEOJSON_DESCRIPTION": {
        "fr": "Un format GeoJSON avec chaque polygone représentant une zone de terre qui sera bleue si la zone est couverte par le nouveau niveau de la mer ou (facultativement) rouge si la zone est couverte par le nouveau niveau de marée.",
        "en": "A GeoJSON format with each polygon representing an area of land which will be blue if the area is covered by the new sea level or (optionally) red if the area is covered by the new tide level."
    },
    "OUTPUT_GEOJSON_TITLE": {
        "fr": "Cartographie des zones inondables GeoJSON",
        "en": "Flood Mapping GeoJSON"
    },
    "START_MESSAGE": {
        "fr": "Commence bientôt",
        "en": "Starting Soon"
    },
    "TASK_ERROR": {
        "fr": "Erreur lors du démarrage de la tâche",
        "en": "Error starting task"
    },
    "DESCRIPTION_LINK_REL_EN": {
        "fr": "alternate",
        "en": "self"
    },
    "DESCRIPTION_LINK_REL_FR": {
        "fr": "self",
        "en": "alternate"
    }
}

ServiceDefinition.S:
{
	"id": "FloodMapping",
	"version": "1.0.0",
	"title": "{MAIN_TITLE}",
	"description": "{MAIN_DESCRIPTION}",
	"inputs": {
		"bounding_box": {
			"schema": {
				"format": "ogc-bbox",
				"type": "object",
				"required": [
					"bbox"
				],
				"properties": {
					"crs": {
						"default": "http://www.opengis.net/def/crs/OGC/1.3/CRS84",
						"format": "uri",
						"type": "string",
						"enum": [
							"http://www.opengis.net/def/crs/OGC/1.3/CRS84",
							"http://www.opengis.net/def/crs/OGC/0/CRS84h"
						]
					},
					"bbox": {
						"oneOf": [
							{
								"minItems": 4,
								"maxItems": 4
							},
							{
								"minItems": 6,
								"maxItems": 6
							}
						],
						"type": "array",
						"items": {
							"type": "number"
						}
					}
				}
			},
			"minOccurs": 1,
			"description": "{AOI_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{AOI}",
			"required": true
		},
		"source_data": {
			"schema": {
				"type": "string",
				"enum": [
					"hrdem-mosaic-1m",
					"hrdem-mosaic-2m"
				]
			},
			"minOccurs": 1,
			"description": "{SOURCE_DATA_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{SOURCE_DATA_TITLE}",
			"required": true
		},
		"sea_level_rise": {
			"schema": {
				"default": 3,
				"format": "double",
				"type": "number",
				"minimum": 0
			},
			"minOccurs": 1,
			"description": "{SEA_LEVEL_RISE_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{SEA_LEVEL_RISE_TITLE}",
			"required": true
		},
        "include_tide": {
			"schema": {
				"default": "true",
				"type": "string",
                "enum": [
					"true",
					"false"
				]
			},
			"minOccurs": 1,
			"description": "{INCLUDE_TIDE_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{INCLUDE_TIDE_TITLE}",
			"required": true
		}
	},
	"outputs": {
		"flood_mapping_nrcan_tiff": {
			"schema": {
				"contentMediaType": "image/tiff",
				"contentEncoding": "binary",
				"type": "string"
			},
			"minOccurs": 1,
			"description": "{OUTPUT_TIFF_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{OUTPUT_TIFF_TITLE}",
			"required": true
		},
		"flood_mapping_nrcan_shapefile": {
			"schema": {
				"contentMediaType": "application/vnd.shp",
				"contentEncoding": "binary",
				"type": "string"
			},
			"minOccurs": 1,
			"description": "{OUTPUT_SHP_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{OUTPUT_SHP_TITLE}",
			"required": true
		},
		"flood_mapping_nrcan_geojson": {
			"schema": {
				"contentMediaType": "application/geo+json",
				"contentEncoding": "binary",
				"type": "string"
			},
			"minOccurs": 1,
			"description": "{OUTPUT_GEOJSON_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{OUTPUT_GEOJSON_TITLE}",
			"required": true
		},
		"flood_mapping_nrcan_kml": {
			"schema": {
				"contentMediaType": "application/vnd.google-earth.kml+xml",
				"contentEncoding": "binary",
				"type": "string"
			},
			"minOccurs": 1,
			"description": "{OUTPUT_KML_DESCRIPTION}",
			"maxOccurs": 1,
			"title": "{OUTPUT_KML_TITLE}",
			"required": true
		}
	},
	"response": [
		"raw"
	],
	"links": [
		{
			"rel": "{DESCRIPTION_LINK_REL_EN}",
			"href": "https://{HOST}/Prod/processes/FloodMapping?lang=en",
			"type": "application/json",
			"title": "Process Description for Flood Mapping",
            "hreflang": "en"
		},
        {
			"rel": "{DESCRIPTION_LINK_REL_FR}",
			"href": "https://{HOST}/Prod/processes/FloodMapping?lang=fr",
			"type": "application/json",
			"title": "FR_Process Description for Flood Mapping",
            "hreflang": "fr"
		},
		{
			"rel": "http://www.opengis.net/def/rel/ogc/1.0/execute",
			"href": "https://{HOST}/Prod/processes/FloodMapping/execution?lang=en",
			"title": "Process Execution for Flood Mapping",
            "hreflang": "en"
		},
        {
			"rel": "http://www.opengis.net/def/rel/ogc/1.0/execute",
			"href": "https://{HOST}/Prod/processes/FloodMapping/execution?lang=fr",
			"title": "FR_Process Execution for Flood Mapping",
            "hreflang": "fr"
		}
	],
	"outputTransmission": [
		"reference"
	],
	"jobControlOptions": [
		"async-execute"
	]
}
